sys_script.script:
(function executeRule(current, previous /*null when async*/) {
	var array = (current.disabled_plans.toString()).split(',');
	var license = (current.license_to_add.toString()).split(',');
	var gr = new GlideRecord("x_821227_vodafon_0_vodafonepocplans");
	var gr2 = new GlideRecord("x_821227_vodafon_0_vodafonepocplans");
	var array2 = [];
	var array3 = [];

	for(var i = 0; i < array.length; i++) {
		var sysId = array[i];
		gs.info("System_Id:" + sysId.toString());

		gr.get(sysId.toString());

		array2.push(gr.sku_id.toString());
	}
	current.disabled_plans = JSON.stringify(array2);

	for(var x = 0; x < license.length; x++) {
		var licenseId = license[x];
		gs.info("License ID:" + licenseId.toString());
		gr2.get(licenseId.toString());
		array3.push(gr2.sku_id.toString());
	}
	current.license_to_add = JSON.stringify(array3);

})(current, previous);
